import React, { useState } from "react";
import {
  Box,
  Button,
  Collapsible,
  Header,
  Heading,
  Grid,
  Grommet,
  Layer,
  Main,
  ResponsiveContext,
  Text,
} from 'grommet';
import { hpe } from 'grommet-theme-hpe';
const AppContainer = ({ ...rest }) => {
  const size = React.useContext(ResponsiveContext);
  return (
    <Box
      direction={size === 'small' ? 'column-reverse' : 'row'}
      fill
      margin="auto"
      width={{ max: 'xxlarge' }}
      {...rest}
    />
  );
};
function App() {  
  return (
    <Grommet theme={hpe} full>
      <ResponsiveContext.Consumer>
        {size => (
          <AppContainer>
          <Box flex overflow="auto">
            <Box height={{ min: '100%' }}>
              <Header
                background="background-front"
                fill="horizontal"
                pad="small"                
                height="xxsmall"
              >
                <Text weight="bold" color="text-strong">
                  Header
                </Text>
              </Header>
              <Main
                fill={undefined}
                flex={false}
                pad="small"                
              >
                <Text weight="bold" color="text-strong">
                  Main
                </Text>
              </Main>
            </Box>
          </Box>
        </AppContainer>
        )}
      </ResponsiveContext.Consumer>
    </Grommet>
  );
}

export default App;
