import React from 'react';
import HeaderExample from './header';
import TabsExample from './tabs';
import {
  Box,
  Button,  
  Header,  
  Grid,
  Grommet,
  Keyboard,  
  Main,
  ResponsiveContext,
  Tab,
  Tabs,
  Text,
  TextInput,
} from 'grommet';
import { hpe } from 'grommet-theme-hpe';

const AppContainer = ({ ...rest }) => {
  const size = React.useContext(ResponsiveContext);
  return (
    <Grid    
    areas={[
      ['HeaderExample'],
      ['Main'],      
    ]}
    fill
  >
    <Box
      direction={size === 'small' ? 'column-reverse' : 'row'}
      fill
      margin="auto"
      width={{ max: 'xxlarge' }}
      {...rest}
    />
    </Grid>
  );
};

function App() {  
  return (
    <Grommet theme={hpe} full>
      <ResponsiveContext.Consumer>
        {size => (
          <AppContainer>
          <Box flex overflow="auto">
            <Box height={{ min: '100%' }}>
              <HeaderExample>               
              </HeaderExample>
              <Main
                fill={undefined}
                flex={false}
                pad="small"                
              >
                <Text weight="bold" color="text-strong">
                  Main
                </Text>
              </Main>
            </Box>
          </Box>
        </AppContainer>
        )}
      </ResponsiveContext.Consumer>
    </Grommet>
  );
}

export default App;
