import React, { useState } from "react";
import {
  Box,
  Button,
  Collapsible,
  Heading,
  Grid,
  Grommet,
  Layer,
  ResponsiveContext,
  Text,
} from 'grommet';
import { hpe } from 'grommet-theme-hpe';
function App() {  
  return (
    <Grommet theme={hpe} full>
      <ResponsiveContext.Consumer>        
          <Grid
          rows={['xxsmall', 'flex', 'xxsmall']}
          columns={['xsmall', 'auto']}
          areas={[
            ['header', 'header'],
            ['sidebar', 'main'],
            ['footer', 'footer'],
          ]}
          fill
        >
          <Box
      border={{ color: 'border', style: 'dashed' }}
      gridArea="header"
      justify="center"
      pad="small"
    >
      <Text weight="bold">Header</Text>
    </Box>
    <Box
      border={{ color: 'border', style: 'dashed' }}
      gridArea="main"
      pad="small"
    >
      <Text weight="bold">Main</Text>
    </Box>

          </Grid>

      </ResponsiveContext.Consumer>
    </Grommet>
  );
}

export default App;
