import React, { useState } from "react";
import { Box, Grid, Text } from 'grommet';
import { hpe } from 'grommet-theme-hpe';
import { FormClose, Notification } from 'grommet-icons';
const AppBar = (props) => (
  <Box
    tag='header'
    direction='row'
    align='center'
    justify='between'
    background='brand'
    pad={{ left: 'medium', right: 'small', vertical: 'small' }}
    elevation='medium'
    style={{ zIndex: '1' }}
    {...props}
  />
);
function App() {

  return (
    <Grommet theme={hpe} full>

        {size => (
          <Grid
          rows={['xxsmall', 'flex', 'xxsmall']}
          columns={['xsmall', 'auto']}
          areas={[
            ['header', 'header'],
            ['sidebar', 'main'],
            ['footer', 'footer'],
          ]}
          fill
        >
          <Box
            border={{ color: 'border', style: 'dashed' }}
            gridArea="header"
            justify="center"
            pad="small"
          >
            <Text weight="bold">Header</Text>
          </Box>

          <Box
            border={{ color: 'border', style: 'dashed' }}
            gridArea="sidebar"
            pad="small"
          >
            <Text weight="bold">Sidebar</Text>
          </Box>

          <Box
            border={{ color: 'border', style: 'dashed' }}
            gridArea="main"
            pad="small"
          >
            <Text weight="bold">Main</Text>
          </Box>

          <Box
            border={{ color: 'border', style: 'dashed' }}
            gridArea="footer"
            justify="center"
            pad="small"
          >
            <Text weight="bold">Footer</Text>
          </Box>
        </Grid>
        )}      
    </Grommet>
  );
}

export default App;
